<script setup lang="ts">
import { computed } from 'vue'
import MModal from '~/src/shared/ui/molecules/m-modal/m-modal.vue'
import AButton from '~/src/shared/ui/atoms/a-button/a-button.vue'
import { Icon } from '~/src/shared/constants/icons'
import AIcon from '~/src/shared/ui/atoms/a-icon/a-icon.vue'
import { termsList } from '~/src/features/f-terms-modal/constants'
import print from 'print-js'
import { useNuxtApp } from '#app'

const MAX_HEIGHT = 780

const props = withDefaults(
  defineProps<{
    modelValue: boolean
    title?: string
    download?: boolean
  }>(),
  {
    modelValue: false,
    title: 'Information',
    download: true,
  },
)

const contentRef = ref(null)
const emit = defineEmits(['update:modelValue', 'close', 'accept', 'download'])

const isOpenModal = computed({
  get() {
    return props.modelValue
  },
  set(value) {
    emit('update:modelValue', value)
  },
})

const { $app } = useNuxtApp()

const accept = () => {
  isOpenModal.value = false
  emit('accept')
}
const onDownload = () => {
  print({ printable: 'termsRef', documentTitle: 'Terms and conditions', type: 'html' })
}
const close = () => {
  isOpenModal.value = false
  emit('close')
}
</script>

